mapper.select:
SELECT A.UPPR_CODE , A.UPPR_CODE_NAME , A.CONFIG_YN , A.INPUT_YN , A.USE_YN From UPPR_CODE A WHERE 1 = 1
AND A.UPPR_CODE_NAME LIKE '%' + #{upperCodeName} + '%'
AND A.INPUT_YN = #{inputYn}
AND A.CMGRP_CD = #{cmgrpCd}
ORDER BY A.UPPR_CODE

	    SELECT
		        COUNT(*)  CNT
		FROM    UPPR_CODE   A 
		WHERE   A.UPPR_CODE = #{upprCode}

		AND 	A.USE_YN = #{useYn}


        AND     A.CMGRP_CD         = #{cmgrpCd}

SELECT A.CODE , A.CODE_NAME , A.VAL1 , A.VAL2 , A.VAL3 , A.SORT_NO , A.USE_YN FROM CODE A WHERE A.UPPR_CODE = #{upprCode}
AND A.USE_YN = #{useYn}
AND A.VAL1 = #{val1}
AND A.VAL2 LIKE '%' + #{val2} + '%'
AND A.CODE = #{code}
AND A.CODE_NAME LIKE '%' + #{codeName}+ '%'
AND A.CMGRP_CD = #{cmgrpCd}
ORDER BY A.UPPR_CODE , A.SORT_NO

SELECT A.CODE , A.CODE_NAME , A.VAL1 , A.VAL2 , A.VAL3 , A.SORT_NO , A.USE_YN FROM V_CODE A WHERE A.UPPR_CODE = #{upprCode} AND A.USE_YN = 'Y'
AND A.CMGRP_CD = #{cmgrpCd}
ORDER BY A.SORT_NO

	    SELECT
		        COUNT(*)  CNT
		FROM    CODE    A 
		WHERE   A.UPPR_CODE = #{upprCode}
		AND     A.CODE      = #{code}

		AND 	A.USE_YN = #{useYn}


        AND     A.CMGRP_CD         = #{cmgrpCd}

	    SELECT
		        COUNT(*)  CNT
		FROM    CODE    A 
		WHERE   A.UPPR_CODE = #{upprCode}

        AND     A.CMGRP_CD         = #{cmgrpCd}

mapper.insert:
		MERGE INTO UPPR_CODE A
		USING (SELECT 'A' AS A) B
		ON    A.UPPR_CODE = #{upprCode}

        AND     A.CMGRP_CD         = #{cmgrpCd}

		WHEN    MATCHED THEN
		        UPDATE
		        SET
		            UPPR_CODE_NAME  = #{upprCodeName}
		        ,	INPUT_YN		= #{inputYn}
		        ,   CONFIG_YN       = #{configYn}
		        ,   USE_YN          = #{useYn}
		        ,   UPDATE_BY       = #{loginUserId}
		        ,   UPDATE_DATE     = NOW()

		        ,	CMGRP_CD         = #{cmgrpCd}

		        ,   TRLG_KEY_VAL		= NULL
		        ,	LT_CHPR_ID		=	#{loginUserId}
		        ,	LT_CH_DTTI		= FN_DATE_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS') 
		WHEN    NOT MATCHED THEN
		        INSERT
		        (
		            UPPR_CODE
		        ,   UPPR_CODE_NAME
		        ,   CONFIG_YN
		        ,	INPUT_YN
		        ,   USE_YN
		        ,   CREATE_BY
		        ,   CREATE_DATE

		        ,	CMGRP_CD

		        ,	TRLG_KEY_VAL
		        ,	LT_CHPR_ID
		        ,	LT_CH_DTTI
		        )
		        VALUES
		        (
		            #{upprCode}
		        ,   #{upprCodeName}
		        ,   #{configYn}
		        ,	#{inputYn}
		        ,   #{useYn}
		        ,   #{loginUserId}
		        ,   NOW()

		        ,	#{cmgrpCd}

		        ,	NULL
		        ,	#{loginUserId}
		        ,	FN_DATE_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')
		        );

		MERGE INTO CODE A
		USING (SELECT 'A' AS A) B
		ON  A.CODE = #{code} AND A.UPPR_CODE = #{upprCode}

        AND     A.CMGRP_CD         = #{cmgrpCd}

		WHEN    MATCHED THEN
		        UPDATE
		        SET
		            CODE_NAME   = #{codeName}
		        ,   VAL1        = #{val1}
		        ,   VAL2        = #{val2}
		        ,   VAL3        = #{val3}
		        ,   SORT_NO     = #{sortNo}
		        ,   USE_YN      = #{useYn}
		        ,   UPDATE_BY   = #{loginUserId}
		        ,   UPDATE_DATE     = NOW()

		        ,	CMGRP_CD         = #{cmgrpCd}

		        ,   TRLG_KEY_VAL		= NULL
		        ,	LT_CHPR_ID		=	#{loginUserId}
		        ,	LT_CH_DTTI		= FN_DATE_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS') 
		WHEN    NOT MATCHED THEN
		        INSERT
		        (
		            CODE
		        ,   CODE_NAME
		        ,   UPPR_CODE
		        ,   VAL1
		        ,   VAL2
		        ,   VAL3
		        ,   SORT_NO
		        ,   USE_YN
		        ,   CREATE_BY
		        ,   CREATE_DATE

		        ,	CMGRP_CD

		        ,	TRLG_KEY_VAL
		        ,	LT_CHPR_ID
		        ,	LT_CH_DTTI
		        )
		        VALUES
		        (
		            #{code}
		        ,   #{codeName}
		        ,   #{upprCode}
		        ,   #{val1}
		        ,   #{val2}
		        ,   #{val3}
		        ,   #{sortNo}
		        ,   #{useYn}
		        ,   #{loginUserId}
		        ,   NOW()

		        ,	#{cmgrpCd}

		        ,	NULL
		        ,	#{loginUserId}
		        ,	FN_DATE_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')
		        );

mapper.delete:
		DELETE FROM UPPR_CODE
		WHERE  UPPR_CODE = #{upprCode}

        AND     CMGRP_CD         = #{cmgrpCd}

		DELETE FROM CODE
		WHERE  UPPR_CODE = #{upprCode}

        AND     CMGRP_CD         = #{cmgrpCd}

		;

		DELETE FROM CODE
		WHERE  UPPR_CODE = #{upprCode}
		AND  CODE = #{code}

        AND     CMGRP_CD         = #{cmgrpCd}

mapper.update:
		UPDATE 	CODE
		SET		VAL1 = #{val1}
		WHERE	UPPR_CODE = #{upprCode}
		AND		CODE = #{code}

        AND    .CMGRP_CD         = #{cmgrpCd}
