mapper.insert:
merge   into esg_cnsltnt_co   a
using   (select 'a' a)        b
on      a.user_id   = #{userId}
and     a.co_id     = #{coId}
when    not matched then
        insert
        (
            user_id
        ,   co_id
        )
        values
        (
            #{userId}
        ,   #{coId}
        )
;

mapper.delete:
delete from esg_cnsltnt_co
where   user_id = #{userId}
and     co_id   = #{coId}
