mapper.select:
SELECT A.MENU_ID , A.MENU_NM , A.MENU_PATH , A.MENU_URL , A.SORT_NO , A.USE_YN , A.SOURCE_DIV FROM MENU A WHERE 1=1
AND A.MENU_NM LIKE '%' + #{menuName} + '%'
AND A.UPPR_MENU_ID IS NULL
AND A.UPPR_MENU_ID = #{upprMenuId}
AND A.MENU_ID = #{menuId}
ORDER BY A.SORT_NO

mapper.insert:
MERGE INTO MENU A
USING (SELECT 'A' AS A) B
ON  A.MENU_ID = #{menuId}
WHEN    MATCHED THEN
        UPDATE
        SET
            MENU_NM         = #{menuNm}

        ,   MENU_PATH       = #{menuPath}


        ,   MENU_URL        = #{menuUrl}

        ,   UPPR_MENU_ID    = #{upprMenuId}
        ,   SORT_NO         = FN_TO_NUMBER(#{sortNo})
        ,   USE_YN          = #{useYn}
        ,   UPDATE_BY       = #{loginUserId}
        ,   UPDATE_DATE     = NOW()
WHEN    NOT MATCHED THEN
        INSERT
        (
            MENU_ID
        ,   MENU_NM

        ,   MENU_PATH


        ,   MENU_URL

        ,   UPPR_MENU_ID
        ,   SORT_NO
        ,   USE_YN
        ,   CREATE_BY
        ,   CREATE_DATE
        )
        VALUES
        (
            #{menuId}
        ,   #{menuNm}

        ,   #{menuPath}


        ,   #{menuUrl}

        ,   #{upprMenuId}
        ,   FN_TO_NUMBER(#{sortNo})
        ,   #{useYn}
        ,   #{loginUserId}
        ,   NOW()
        );

mapper.delete:
DELETE MENU
WHERE  MENU_ID = #{upprMenuId}
OR     UPPR_MENU_ID = #{upprMenuId}
