mapper.select:
SELECT CASE WHEN A.ROLE_ID IS NULL THEN '0' ELSE '1' END CHK , A.ROLE_ID , A.CREATE_ROLE , A.UPDATE_ROLE , A.DELETE_ROLE , B.MENU_ID , B.MENU_NM , B.UPPR_MENU_ID , ( SELECT MENU_NM FROM MENU WHERE MENU_ID = B.UPPR_MENU_ID ) UPPR_MENU_NM FROM MENU B LEFT OUTER JOIN ROLE_PRIV A ON B.MENU_ID = A.MENU_ID AND A.ROLE_ID = #{roleId} WHERE B.USE_YN = 'Y' AND B.UPPR_MENU_ID IS NOT NULL
AND A.CMGRP_CD = #{cmgrpCd}
ORDER BY B.SORT_NO

SELECT A.USER_ID , A.USER_NAME , A.DEPT_CODE , A.DEPT_NAME , A.ROLE_ID , (SELECT ROLE_NM FROM ROLE WHERE ROLE_ID = A.ROLE_ID) ROLE_NM , A.ACCESS_IP FROM USER_INFO A
AND A.CMGRP_CD = #{cmgrpCd}
ORDER BY A.DEPT_CODE , A.USER_ID

SELECT
        COUNT(*) CNT
FROM    ROLE
WHERE   ROLE_ID = #{popRoleId}

AND     A.CMGRP_CD         = #{cmgrpCd}

SELECT
        COUNT(*) CNT
FROM    USER_INFO
WHERE   USER_ID = #{userId}

AND     A.CMGRP_CD         = #{cmgrpCd}

SELECT MENU_NM, MENU_PATH, MENU_ID FROM Menu WHERE UPPR_MENU_ID is not null AND USE_YN = 'Y'
AND A.CMGRP_CD = #{cmgrpCd}
ORDER BY SORT_NO

mapper.insert:
        MERGE   INTO ROLE A
		USING   (SELECT 'A' AS A) B
		ON      A.ROLE_ID   = #{popRoleId}

        AND     A.CMGRP_CD         = #{cmgrpCd}

		WHEN    MATCHED THEN
		        UPDATE
		        SET
		            ROLE_NM     = #{popRoleName}
		        ,	LOGIN_PAGE	= #{popLogin}

		        ,   UPDATE_BY   = #{loginUserId}
		        ,   UPDATE_DATE     = NOW()

		        ,	CMGRP_CD         = #{cmgrpCd}

		        ,   TRLG_KEY_VAL		= NULL
		        ,	LT_CHPR_ID		=	#{loginUserId}
		        ,	LT_CH_DTTI		= FN_DATE_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')  
		WHEN    NOT MATCHED THEN
		        INSERT
		        (
		            ROLE_ID
		        ,   ROLE_NM
		        ,	LOGIN_PAGE

		        ,   CREATE_BY
		        ,   CREATE_DATE

		        ,	CMGRP_CD

		        ,	TRLG_KEY_VAL
		        ,	LT_CHPR_ID
		        ,	LT_CH_DTTI
		        )
		        VALUES
		        (
		            #{popRoleId}
		        ,   #{popRoleName}
		        ,	#{popLogin}

		        ,   #{loginUserId}
		        ,   NOW()

		        ,	#{cmgrpCd}

		        ,	NULL
		        ,	#{loginUserId}
		        ,	FN_DATE_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')
		        );

        MERGE INTO ROLE_PRIV A
        USING (SELECT 'A' AS A) B
        ON  A.ROLE_ID = #{roleId} AND A.MENU_ID = #{menuId}

        AND     A.CMGRP_CD         = #{cmgrpCd}

        WHEN    MATCHED AND #{chk} = '0' THEN
                DELETE
        WHEN    NOT MATCHED THEN
                INSERT
                (
                    ROLE_ID

                ,	CREATE_ROLE


                ,	UPDATE_ROLE


                ,	DELETE_ROLE

                ,   MENU_ID
                ,   CREATE_BY
                ,   CREATE_DATE

		        ,	CMGRP_CD

                )
                VALUES
                (
                    #{roleId}

                ,	#{createRole}


                ,	#{updateRole}


                ,	#{deleteRole}

                ,   #{menuId}
                ,   #{loginUserId}
                ,   NOW()

		        ,	#{cmgrpCd}

                );

         MERGE INTO ROLE_PRIV A
        USING (SELECT 'A' AS A) B
        ON  A.ROLE_ID = #{roleId} AND A.MENU_ID = #{menuId}

        AND     A.CMGRP_CD         = #{cmgrpCd}

        WHEN    MATCHED AND #{chk} = '0' THEN
                DELETE
        WHEN    MATCHED AND #{chk} = '1' THEN
                UPDATE
                SET
                	CREATE_ROLE = #{createRole}
                ,	UPDATE_ROLE = #{updateRole}
                ,	DELETE_ROLE = #{deleteRole}
                ,   UPDATE_BY   = #{loginUserId}
		        ,   UPDATE_DATE     = NOW()

		        ,	CMGRP_CD         = #{cmgrpCd}

		        ,   TRLG_KEY_VAL		= NULL
		        ,	LT_CHPR_ID		=	#{loginUserId}
		        ,	LT_CH_DTTI		= FN_DATE_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')  
        WHEN    NOT MATCHED THEN
                INSERT
                (
                    ROLE_ID

                ,	CREATE_ROLE


                ,	UPDATE_ROLE


                ,	DELETE_ROLE

                ,   MENU_ID
                ,   CREATE_BY
                ,   CREATE_DATE

		        ,	CMGRP_CD

                ,	TRLG_KEY_VAL
		        ,	LT_CHPR_ID
		        ,	LT_CH_DTTI
                )
                VALUES
                (
                    #{roleId}

                ,	#{createRole}


                ,	#{updateRole}


                ,	#{deleteRole}

                ,   #{menuId}
                ,   #{loginUserId}
                ,   NOW()

		        ,	#{cmgrpCd}

                ,	NULL
		        ,	#{loginUserId}
		        ,	FN_DATE_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')
                );

        MERGE   INTO USER_INFO  A
		USING   (SELECT 'A' AS A) B
		ON      A.USER_ID       = #{userId}

        AND     A.CMGRP_CD         = #{cmgrpCd}

		WHEN    MATCHED THEN
		        UPDATE
		        SET
		            ACCESS_IP   = #{accessIp}
		        ,   ROLE_ID     = #{role}
		        ,   UPDATE_BY   = #{loginUserId}
		        ,   UPDATE_DATE     = NOW()

		        ,	CMGRP_CD         = #{cmgrpCd}

		        ,   TRLG_KEY_VAL		= NULL
		        ,	LT_CHPR_ID		=	#{loginUserId}
		        ,	LT_CH_DTTI		= FN_DATE_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')  
		WHEN    NOT MATCHED THEN
		        INSERT
		        (
		            USER_ID
		        ,   USER_NAME
		        ,   DEPT_CODE
		        ,   DEPT_NAME
		        ,   ACCESS_IP
		        ,   ROLE_ID
		        ,   CREATE_BY
		        ,   CREATE_DATE

		        ,	CMGRP_CD

		        ,	TRLG_KEY_VAL
		        ,	LT_CHPR_ID
		        ,	LT_CH_DTTI
		        )
		        VALUES
		        (
		            #{userId}
		        ,   #{userName}
		        ,   #{deptCode}
		        ,   #{deptName}
		        ,   #{accessIp}
		        ,   #{role}
		        ,   #{loginUserId}
		        ,   NOW()

		        ,	#{cmgrpCd}

		        ,	NULL
		        ,	#{loginUserId}
		        ,	FN_DATE_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')
		        );

mapper.delete:
DELETE  FROM ROLE
WHERE   USE_YN  =  'Y'
AND     ROLE_ID = #{roleId}

AND     CMGRP_CD         = #{cmgrpCd}

DELETE  FROM USER_INFO
WHERE   USER_ID  =  #{userId}
AND     USER_NAME = #{userName}

AND     CMGRP_CD         = #{cmgrpCd}
