mapper.select:
        SELECT
                A.EVENT
        ,       FN_TO_CHAR(A.ACT_DATE,'YYYY-MM-DD HH24:MI:SS') ACT_DATE
        ,       A.IP
        FROM    USER_ACT_LOG    A 
        WHERE   A.USER_ID   = #{userId}

        AND     ACT_YMD BETWEEN #{startYmd} AND #{endYmd}


		AND		A.CMGRP_CD 		= #{cmgrpCd} 

        ORDER   BY
                A.ACT_DATE DESC

        SELECT
        		CREATE_ROLE
        ,		UPDATE_ROLE
        ,		DELETE_ROLE
        FROM	ROLE_PRIV
        WHERE  	ROLE_ID	=	#{roleId}
        AND		MENU_ID	= 	#{menuId}

		AND		CMGRP_CD 		= #{cmgrpCd}

mapper.insert:
INSERT INTO USER_ACT_LOG( USER_ID, ACT_DATE, EVENT, IP, ACT_YMD
,CMGRP_CD
)VALUES( #{loginUserId}, NOW(), #{event}, #{ip}, FN_TO_CHAR(NOW(), 'YYYYMMDD')
,#{cmgrpCd}
)
