mapper.select:
SELECT A.ID , A.STEP , A.TITLE , A.CONTENTS , A.CONTENT_DETAIL , CASE WHEN A.TRAINING_FILE_URL IS NOT NULL THEN SUBSTRING(A.TRAINING_FILE_URL,FN_LEN(A.TRAINING_FILE_URL) - FN_STRINDEX('\',FN_REVERSE(A.TRAINING_FILE_URL))+2,FN_LEN(A.TRAINING_FILE_URL)) ELSE NULL END TRAINING_FILE_URL , TRAINING_FILE_URL TRAINING_FILE_URL_PATH , A.VIDEO_URL
, A.UP_ID , (SELECT TITLE FROM EDUCATION B WHERE B.ID = A.ID) UP_TITLE
, A.CMGRP_CD FROM EDUCATION A WHERE A.STEP = #{step}
AND A.UP_ID = #{upId}
AND A.ID = #{id}
AND CMGRP_CD = #{loginCmgrpCd}

mapper.insert:
        MERGE INTO EDUCATION A
        USING (SELECT 'A' AS A) B
        ON  A.ID = #{id}
        WHEN    MATCHED THEN
                UPDATE
                SET
                    TITLE               = #{title}
                ,   CONTENTS            = #{contents}
                ,	CONTENT_DETAIL		= #{contentDetail}
                ,   TRAINING_FILE_URL   = #{trainingFileUrl}
                ,   VIDEO_URL           = #{videoUrl}
                ,   UPDATE_BY           = #{loginUserId}
                ,   UPDATE_DATE     = NOW()
		        ,   TRLG_KEY_VAL		= NULL
		        ,	LT_CHPR_ID		=	#{loginUserId}
		        ,	LT_CH_DTTI		= FN_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')  
		        ,   CMGRP_CD	   = #{loginCmgrpCd}
        WHEN    NOT MATCHED THEN
                INSERT
                (
                	STEP
                ,    TITLE
                ,   CONTENTS
                ,	CONTENT_DETAIL
                ,   TRAINING_FILE_URL
                ,   VIDEO_URL
                ,	UP_ID
                ,   CREATE_BY
                ,   CREATE_DATE
                ,	TRLG_KEY_VAL
		        ,	LT_CHPR_ID
		        ,	LT_CH_DTTI
		        ,	CMGRP_CD
                )
                VALUES
                (
                	#{step}
                ,   #{title}
                ,   #{contents}
                ,	#{contentDetail}
                ,   #{trainingFileUrl}
                ,   #{videoUrl}
                ,	#{upId}
                ,   #{loginUserId}
                ,   NOW()
                ,	NULL
		        ,	#{loginUserId}
		        ,	FN_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')
		        ,	#{loginCmgrpCd}
                );

mapper.delete:
DELETE  FROM EDUCATION
WHERE   ID = #{id}

AND	   CMGRP_CD	   = #{loginCmgrpCd}
;

DELETE  FROM EDUCATION
WHERE   UP_ID = #{id}

AND	   CMGRP_CD	   = #{loginCmgrpCd};

mapper.update:
    	UPDATE  EDUCATION
    	SET
    			TRAINING_FILE_URL = NULL,
    			VIDEO_URL = NULL,
    			UPDATE_DATE     = NOW()
		        ,   TRLG_KEY_VAL		= NULL
		        ,	LT_CHPR_ID		=	#{loginUserId}
		        ,	LT_CH_DTTI		= FN_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')  
        WHERE   ID = #{id}

        AND	   CMGRP_CD	   = #{loginCmgrpCd}
