mapper.select:
WITH WW AS ( SELECT A.ID , A.TITLE , A.USER_ID , FN_USER_NAME(A.USER_ID) USER_NAME , FN_TO_CHAR(CREATE_DATE, 'YYYY-MM-DD HH24:MI:SS') CREATE_DATE , A.CONTENTS , A.HIT , FN_TO_CHAR(A.DATE_FROM, 'YYYY-MM-DD') DATE_FROM , FN_TO_CHAR(A.DATE_TO, 'YYYY-MM-DD') DATE_TO , A.BANNER_REGI_YN , A.BANNER_IMG_YN , A.BANNER_IMG_PATH , A.BANNER_DEFAULT_IMG , A.ATTACH_FILE_URL1 , A.ATTACH_FILE_URL2 , A.ATTACH_FILE_URL3 , A.ATTACH_FILE_URL4 , A.ATTACH_FILE_URL5 , A.BBS_CATEGORY , A.BBS_SHOW_YN , LAG(A.TITLE) OVER (ORDER BY ID) PRE_TITLE , LEAD(A.TITLE) OVER (ORDER BY ID) AFT_TITLE , ROW_NUMBER() OVER (ORDER BY ID) RID , CMGRP_CD FROM BBS A WHERE BBS_TYPE = 'A' AND WRITE_YMD > '19900101' AND CMGRP_CD = #{loginCmgrpCd} ) SELECT * FROM WW WHERE 1 =1
AND RID = #{rid}
AND ID = #{id}
AND CMGRP_CD = #{loginCmgrpCd}

mapper.insert:
        MERGE INTO BBS A
        USING (SELECT 'A' AS A) B
        ON  A.ID = #{id}
        WHEN    MATCHED THEN
                UPDATE
                SET
                    TITLE               = #{title}
                ,   CONTENTS            = #{contents}
                ,   DATE_FROM           = #{dateFrom}
                ,   DATE_TO             = #{dateTo}
                ,   BANNER_REGI_YN      = #{bannerRegiYn}
                ,	BANNER_IMG_YN		= #{bannerImgYn}
                ,	BANNER_DEFAULT_IMG	= #{bannerDefaultImg}
                ,   CMGRP_CD	   = #{loginCmgrpCd}

                ,	BANNER_IMG_PATH		= #{bannerImgPath}


                ,   ATTACH_FILE_URL1     = #{attachFileUrl1}


                ,   ATTACH_FILE_URL2     = #{attachFileUrl2}


                ,   ATTACH_FILE_URL3     = #{attachFileUrl3}


                ,   ATTACH_FILE_URL4     = #{attachFileUrl4}


                ,   ATTACH_FILE_URL5     = #{attachFileUrl5}

                ,	BBS_SHOW_YN			= #{bbsShowYn}
                ,	BBS_CATEGORY		= #{bbsCategory}
                ,   UPDATE_BY           = #{loginUserId}
                ,   UPDATE_DATE     = NOW()
		        ,   TRLG_KEY_VAL		= NULL
		        ,	LT_CHPR_ID		=	#{loginUserId}
		        ,	LT_CH_DTTI		= FN_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')  
        WHEN    NOT MATCHED THEN
                INSERT
                (                 
                    TITLE               
                ,   CONTENTS            
                ,   WRITE_YMD           
                ,   USER_ID             
                ,   DATE_FROM           
                ,   DATE_TO             
                ,   BANNER_REGI_YN
                ,	BANNER_IMG_YN 
                ,	BANNER_DEFAULT_IMG

                ,	BANNER_IMG_PATH


                ,   ATTACH_FILE_URL1   


                ,   ATTACH_FILE_URL2   


                ,   ATTACH_FILE_URL3   


                ,   ATTACH_FILE_URL4   


                ,   ATTACH_FILE_URL5   

                ,   BBS_TYPE        
                ,	BBS_CATEGORY    
                ,   CREATE_BY           
                ,   CREATE_DATE        
                ,	TRLG_KEY_VAL
		        ,	LT_CHPR_ID
		        ,	LT_CH_DTTI 
		        ,   CMGRP_CD
                )
                VALUES
                (
                    #{title}
                ,   #{contents}
                ,   FN_TO_CHAR(NOW(), 'YYYYMMDD')
                ,   #{loginUserId}
                ,   FN_TO_CHAR(NOW(), 'YYYY-MM-DD')
                ,   #{dateTo}
                ,   #{bannerRegiYn}
                ,	#{bannerImgYn}
                ,	#{bannerDefaultImg}

                ,	#{bannerImgPath}


                ,   #{attachFileUrl1}


                ,   #{attachFileUrl2}


                ,   #{attachFileUrl3}


                ,   #{attachFileUrl4}


                ,   #{attachFileUrl5}

                ,   'A'
                ,	#{bbsCategory}
                ,   #{loginUserId}
                ,   NOW()
                ,	NULL
		        ,	#{loginUserId}
		        ,	FN_TO_CHAR(NOW(),'YYYYMMDDHHMISSMS')
		        ,   #{loginCmgrpCd}
                );

mapper.delete:
DELETE  FROM BBS
WHERE   ID = #{id}
AND	   CMGRP_CD	   = #{loginCmgrpCd}
