mapper.select:
    SELECT
	        COUNT(*)  CNT
	FROM    UPPR_CODE   A 
	WHERE   A.UPPR_CODE = #{upprCode}

	AND 	A.USE_YN = #{useYn}

SELECT A.CODE , A.CODE_NAME , A.VAL1 , A.VAL2 , A.VAL3 , A.SORT_NO , A.USE_YN FROM CODE A WHERE A.UPPR_CODE = #{upprCode}
AND A.USE_YN = #{useYn}
AND A.VAL1 = #{val1}
AND A.VAL2 LIKE '%' + #{val2} + '%'
AND A.CODE = #{code}
AND A.CODE_NAME LIKE '%' + #{codeName}+ '%'
ORDER BY A.UPPR_CODE , A.SORT_NO

    SELECT
	        COUNT(*)  CNT
	FROM    CODE    A 
	WHERE   A.UPPR_CODE = #{upprCode}
	AND     A.CODE      = #{code}

	AND 	A.USE_YN = #{useYn}

mapper.insert:
MERGE INTO UPPR_CODE A
USING (SELECT 'A' AS A) B
ON    A.UPPR_CODE = #{upprCode}
WHEN    MATCHED THEN
        UPDATE
        SET
            UPPR_CODE_NAME  = #{upprCodeName}
        ,	INPUT_YN		= #{inputYn}
        ,   CONFIG_YN       = #{configYn}
        ,   USE_YN          = #{useYn}
        ,   UPDATE_BY       = #{loginUserId}
        ,   UPDATE_DATE     = NOW()
WHEN    NOT MATCHED THEN
        INSERT
        (
            UPPR_CODE
        ,   UPPR_CODE_NAME
        ,   CONFIG_YN
        ,	INPUT_YN
        ,   USE_YN
        ,   CREATE_BY
        ,   CREATE_DATE
        )
        VALUES
        (
            #{upprCode}
        ,   #{upprCodeName}
        ,   #{configYn}
        ,	#{inputYn}
        ,   #{useYn}
        ,   #{loginUserId}
        ,   NOW()
        );

MERGE INTO CODE A
USING (SELECT 'A' AS A) B
ON  A.CODE = #{code} AND A.UPPR_CODE = #{upprCode}
WHEN    MATCHED THEN
        UPDATE
        SET
            CODE_NAME   = #{codeName}
        ,   VAL1        = #{val1}
        ,   VAL2        = #{val2}
        ,   VAL3        = #{val3}
        ,   SORT_NO     = FN_TO_NUMBER(#{sortNo})
        ,   USE_YN      = #{useYn}
        ,   UPDATE_BY   = #{loginUserId}
        ,   UPDATE_DATE     = NOW()
WHEN    NOT MATCHED THEN
        INSERT
        (
            CODE
        ,   CODE_NAME
        ,   UPPR_CODE
        ,   VAL1
        ,   VAL2
        ,   VAL3
        ,   SORT_NO
        ,   USE_YN
        ,   CREATE_BY
        ,   CREATE_DATE
        )
        VALUES
        (
            #{code}
        ,   #{codeName}
        ,   #{upprCode}
        ,   #{val1}
        ,   #{val2}
        ,   #{val3}
        ,   FN_TO_NUMBER(#{sortNo})
        ,   #{useYn}
        ,   #{loginUserId}
        ,   NOW()
        );

mapper.delete:
DELETE FROM CODE
WHERE  UPPR_CODE = #{upprCode}
AND  CODE = #{code}

mapper.update:
UPDATE 	CODE
SET		VAL1 = #{val1}
WHERE	UPPR_CODE = #{upprCode}
AND		CODE = #{code}
